import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {saveSpiel} from '../../services/SpieleService.js';

import { Image, ButtonToolbar, ButtonGroup, Button, FormGroup, FormControl} from 'react-bootstrap';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash, faUserFriends, faClock, faBan, faSave } from '@fortawesome/free-solid-svg-icons'
library.add(faEdit, faTrash, faUserFriends, faClock, faBan, faSave)


export default class SpielKarteNeu extends Component {

  constructor() {
      super();
      this.state = {
        isToggleOn: false,
        neuesSpiel: {
          titel: "Test_ORG",
          autor: "Rudi Hoffmann",
          mitspieler: 1,
          dauer: 10
        },
      };

      // This binding is necessary to make `this` work in the callback
      this.handleSave = this.handleSave.bind(this);
      this.handleTitel = this.handleTitel.bind(this);
      this.handleAutor = this.handleAutor.bind(this);
      this.handleMitspieler = this.handleMitspieler.bind(this);
      this.handleDauer = this.handleDauer.bind(this);
  }

  handleSave(e){
    this.setState(state => ({
      isToggleOn: !this.isToggleOn,
    }));

    e.preventDefault();

    saveSpiel(this.state.neuesSpiel);
  }

  handleTitel(e){
    this.setState(
      prevState =>({
        neuesSpiel: {
          ...prevState.neuesSpiel,
          titel: e.target.value
        }
      }),
      () => console.log(this.state.neuesSpiel)
    );
      //{ neuesSpiel: {titel: e.target.value} });
  }
  handleAutor(e){
    this.setState({ neuesSpiel: {autor: e.target.value} });
  }
  handleMitspieler(e){
    this.setState({ neuesSpiel: {mitspieler: e.target.value} });
  }
  handleDauer(e){
    this.setState({ neuesSpiel: {dauer: e.target.value} });
  }


  render() {

    return(
      <React.Fragment>
      <div>
        {this.isToggleOn ? 'ON': 'OFF'}
      </div>
      <div className="spiel-card-neu">
          <div className="spiel-card-neu card">
            <Image className="card-img-top"
              src={"../images/spiel_0.jpg"} responsive />
              <div className="spiel-card-body card-body ">
                <form>
                  <FormGroup>
                    <FormControl
                      type="text"
                      placeholder="Titel"
                      value={this.state.neuesSpiel.titel}
                      onChange={this.handleTitel}
                    />
                    <FormControl
                      type="text"
                      placeholder="Autor"
                      value={this.state.neuesSpiel.autor}
                      onChange={this.handleAutor}
                    />
                    <FormControl
                      type="text"
                      placeholder="Mitspieler"
                      value={this.state.neuesSpiel.mitspieler}
                      onChange={this.handleMitspieler}
                    />
                    <FormControl
                      type="text"
                      placeholder="Dauer"
                      value={this.state.neuesSpiel.dauer}
                      onChange={this.handleDauer}
                    />
                  </FormGroup>
                </form>
              </div>
              <div>
                <ButtonToolbar className="float-right">
                  <ButtonGroup>
                    <Button>
                      <FontAwesomeIcon icon="ban" />
                    </Button>
                    <Button onClick={this.handleSave}>
                      <FontAwesomeIcon icon="save" />
                    </Button>
                  </ButtonGroup>
                </ButtonToolbar>
              </div>
          </div>
      </div>
      </React.Fragment>
      );
    }
}
