import React, { Component } from 'react';
import logo from './board-game-icon.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      spiele: [],
    }
  }

  componentDidMount() {
    return fetch('/api/spiele')
      .then((response) => response.json())
      .then(responseJson => {
        this.setState({
          spiele: responseJson
        });
      })
  }

  render() {
    var unsereAlle = this.state.spiele.map((map_param_spiele, index) => {
      return (
        <tr key={index}>
          <td>{map_param_spiele.titel}</td>
          <td>{map_param_spiele.dauer}</td>
          <td>{map_param_spiele.mitspieler}</td>
          <td>{map_param_spiele.autor}</td>
        </tr>
      );
    });


    if(unsereAlle){ //race-condition: unsereAlle m端ssen erst definiert sein, bevor wir sie rendern d端rfen
      return(
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" width="100" />
          </header>
          <h2>SPIELE</h2>
          <table class="table table-bordered">
            <thead>
              <tr>
                <th scope="col">Spiel</th>
                <th scope="col">Dauer</th>
                <th scope="col">Mitspieler</th>
                <th scope="col">Autor</th>
              </tr>
            </thead>
            <tbody>
              {unsereAlle}
            </tbody>
          </table>
        </div>
      );
    } else {
      return (<p>Loading content ...</p>);
    }
  }
}

export default App;
