import React, { Component } from 'react';
import logo from './board-game-icon.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      spiele: [],
    }
  }

  componentDidMount() {
    return fetch('/api/spiele')
      .then((response) => response.json())
      .then(responseJson => {
        //console.log(JSON.stringify(responseJson));
        console.log('Geparstes JSON: ',responseJson);
        this.setState({
          //message: responseJson[2].titel
          spiele: responseJson.spiele
        });
      })
  }

  render() {
    //console.log('Hier! message ist:' + this.state.message + 'ENDE');
    const { spiele } = this.state;

    //console.log(spiele[2]);

    if(spiele){
      return (
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" width="300" />
            <h1>GAMES</h1>
            <p>
              Edit <code>src/App.js</code> and save to reload.
            </p>
            <p>
              Nachricht von der API:{' '}
            </p>
            <ul>
              <li>Test</li>
              //<li>{spiele[0].titel}</li>
            </ul>
          </header>
        </div>
      );
    } else {
       return (<p>Loading content ...</p>);
    }
  }
}

export default App;
