import React, { Component } from 'react';
import {saveSpiel} from '../../services/SpieleService.js';

import { Image, ButtonToolbar, ButtonGroup, Button, FormGroup, FormControl} from 'react-bootstrap';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash, faUserFriends, faClock, faBan, faSave } from '@fortawesome/free-solid-svg-icons'
library.add(faEdit, faTrash, faUserFriends, faClock, faBan, faSave)


export default class SpielKarteEdit extends Component {

  constructor(props) {
      super(props);
      this.state = {
        spiele: {},
        neuesSpiel: {
          titel: "",
          autor: "",
          mitspieler: 0,
          dauer: 0
        },
      };

      // This binding is necessary to make `this` work in the callback
      this.handleSave = this.handleSave.bind(this);
      this.handleCancel = this.handleCancel.bind(this);
      this.handleChange = this.handleChange.bind(this);
  }

  handleSave(e){
    saveSpiel(this.state.neuesSpiel);
    this.props.hideModal();
  }

  handleCancel(e){
    this.props.hideModal();
  }

  handleChange(e, field){
    let value = e.target.value;
    this.setState(
      prevState =>({
        neuesSpiel: {
          ...prevState.neuesSpiel,
          [field]: value
        }
      }),
      () => console.log(this.state.neuesSpiel)
    );
  }


  render() {
    {this.state.neuesSpiel.titel = this.props.spiele.titel}
    {this.state.neuesSpiel.autor = this.props.spiele.autor}
    {this.state.neuesSpiel.mitspieler = this.props.spiele.mitspieler}
    {this.state.neuesSpiel.dauer = this.props.spiele.dauer}

    return(
      <React.Fragment>
      <div className="spiel-card-neu">
          <div className="card">
            <Image className="card-img-top"
              src={"../images/spiel_0.jpg"} responsive />
              <div className="spiel-card-body card-body ">
                <form>
                  <FormGroup>
                    <FormControl
                      type="text"
                      placeholder="Titel"
                      value={this.state.neuesSpiel.titel}
                      onValueChange={e => this.handleChange(e,"titel")}
                    />
                    <FormControl
                      type="text"
                      placeholder="Autor"
                      value={this.state.neuesSpiel.autor}
                      onChange={e => this.handleChange(e,"autor")}
                    />
                    <FormControl
                      type="text"
                      placeholder="Mitspieler"
                      value={this.state.neuesSpiel.mitspieler}
                      onChange={e => this.handleChange(e,"mitspieler")}
                    />
                    <FormControl
                      type="text"
                      placeholder="Dauer"
                      value={this.state.neuesSpiel.dauer}
                      onChange={e => this.handleChange(e,"dauer")}
                    />
                  </FormGroup>
                </form>
              </div>
              <div>
                <ButtonToolbar className="float-right">
                  <ButtonGroup>
                    <Button onClick={this.handleCancel}>
                      <FontAwesomeIcon icon="ban" />
                    </Button>
                    <Button onClick={this.handleSave}>
                      <FontAwesomeIcon icon="save" />
                    </Button>
                  </ButtonGroup>
                </ButtonToolbar>
              </div>
          </div>
      </div>
      </React.Fragment>
      );
    }
}
