import React, { Component } from 'react';
import logo from './board-game-icon.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      spiele: [],
    }
  }

  componentDidMount() {
    return fetch('/api/spiele')
      .then((response) => response.json())
      .then(responseJson => {
        this.setState({
          spiele: responseJson
        });
      })
  }

  render() {
    var unsereTitel = this.state.spiele.map(map_param_spiele => {
      return (
        <li>{map_param_spiele.titel}</li>
      )
    });

    var unsereAutoren = this.state.spiele.map(map_param_spiele => {
      return (
        <li>{map_param_spiele.autor}</li>
      )
    });

    if(unsereTitel && unsereAutoren){ //race-condition: unsereTitel... m端ssen erst definiert sein, bevor wir sie rendern d端rfen
      return(
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" width="100" />
            <h2>SPIELE</h2>
          </header>

            <p>
              Edit <code>src/App.js</code> and save to reload.
            </p>
            <p>
              Nachricht von der API:{' '}
            </p>
            <ul>
              <div>{unsereTitel}</div>
              <div>{unsereAutoren}</div>
            </ul>

        </div>
      );
    } else {
      return (<p>Loading content ...</p>);
    }
  }
}

export default App;
