import React, { Component } from 'react';
import Spielliste from './spielliste';
import SpieleService from '../../services/SpieleService';
import { Grid, Row, Col } from 'react-bootstrap';

export default class Spiele extends Component {
  constructor() {
      super();
      this.state = {
          //isLoading: true,
          spiele: [],
          //error: null
      };
  }

// fetchSpiele() {
