import React, { Component } from 'react';
import logo from './board-game-icon.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      spiele: [],
    }
  }

  componentDidMount() {
    return fetch('/api/spiele')
      .then((response) => response.json())
      .then(responseJson => {
        this.setState({
          spiele: responseJson
        });
      })
  }

  render() {
    var unsereTitel = this.state.spiele.map(map_param_spiele => {
      return (
        map_param_spiele.titel
      )
    });

    var unsereDauer = this.state.spiele.map(map_param_spiele => {
      return (
        map_param_spiele.dauer
      )
    });

    var unsereMitspieler = this.state.spiele.map(map_param_spiele => {
      return (
        map_param_spiele.mitspieler
      )
    });

    var unsereAutoren = this.state.spiele.map(map_param_spiele => {
      return (
        map_param_spiele.autor
      )
    });

    if(unsereTitel && unsereAutoren){ //race-condition: unsereTitel... m端ssen erst definiert sein, bevor wir sie rendern d端rfen
      return(
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" width="100" />
          </header>
          <h2>SPIELE</h2>
          <table class="table table-bordered">
            <thead>
              <tr>
                <th scope="col">Spiel</th>
                <th scope="col">Dauer</th>
                <th scope="col">Mitspieler</th>
                <th scope="col">Autor</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th scope="row">{unsereTitel}</th>
                <td>{unsereDauer}</td>
                <td>{unsereMitspieler}</td>
                <td>{unsereAutoren}</td>
              </tr>
            </tbody>
          </table>
          // <ul>
          //   <div>{unsereTitel}</div>
          //   <div>{unsereAutoren}</div>
          // </ul>

        </div>
      );
    } else {
      return (<p>Loading content ...</p>);
    }
  }
}

export default App;
