import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Image, ButtonToolbar, ButtonGroup, Button} from 'react-bootstrap';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash, faUserFriends, faClock } from '@fortawesome/free-solid-svg-icons'
import SpielKarteEdit from './spielkarteedit';

library.add(faEdit, faTrash, faUserFriends, faClock)


const Modal = ({ show, children }) => {
  const showHideClassName = show ? 'modal display-block' : 'modal display-none';
  return (
    <div className={showHideClassName}>
      <section className='modal-main'>
        {children}
      </section>
    </div>
  );
};

export default class SpielKarte extends Component {

  constructor(props) {
      super(props);
      this.state = {
        spiele: {},
        show: false,
      };
    }

    showModal = () => {
      {console.log('show modal in spielkarte')}
      this.setState({ show: true });
    }

    hideModal = () => {
      {console.log('hide modal in spielkarte')}
      this.setState({ show: false });
    }

  render(){

    return(
        <div className="spiel-card">
            <Modal show={this.state.show}> <SpielKarteNeu hideModal={this.hideModal}/></Modal>
            <div onClick={this.showModal} className="spiel-card card">
              {console.log('spielkarte ' + this.props.spiele.id)}
              <Image className="card-img-top"
                src={"../images/spiel_" + this.props.spiele.id + ".jpg"}
                onError={(e)=>{
                  e.target.onerror = null;
                  e.target.src="../images/spiel_0.jpg"
                }} responsive />
                <div className="spiel-card-body card-body ">
                  <h4 className="card-title">{this.props.spiele.titel}</h4>
                  <h6 className="card-subtitle mb-2 text-muted">{this.props.spiele.autor}</h6>
                  <div className="align-self-end">
                    <div className="float-left">
                      <FontAwesomeIcon icon="user-friends" /> {this.props.spiele.mitspieler}
                    </div>
                    <div className="float-right">
                      <FontAwesomeIcon icon="clock" /> {this.props.spiele.dauer} min
                    </div>
                  </div>
                </div>
                <div>
                  <ButtonToolbar className="float-right">
                    <ButtonGroup>
                      <Button>
                        <FontAwesomeIcon icon="edit" />
                      </Button>
                      <Button>
                        <FontAwesomeIcon icon="trash" />
                      </Button>
                    </ButtonGroup>
                  </ButtonToolbar>
                </div>
            </div>
        </div>
      );
    }
}
