import React, { Component } from 'react';
import Spielliste from './spielliste';
import SpieleService from '../../services/SpieleService';

export default class Spiele extends Component {
    constructor() {
        super();
        this.state = {
            spiele: []
        };
    }

    componentDidMount() {
        this.setState(() => ({ spiele: SpieleService.getSpiele() }));
        console.log('aus json' + this.state.spiele);
    }

    render() {
      if(this.state.spiele)
      {
        return (
            <div className="container-fluid" style={{marginLeft: '-15px'}}>
                <div className="d-flex flex-row">
                    <div className="col-sm-12">
                        <Spielliste spiele={this.state.spiele} />
                    </div>
                </div>
            </div>
        );
      }
      else {
        return (<p>Loading content ...</p>);
      }
    }
}
